import React, { useEffect, useState } from "react";
import { auth } from "../firebase";

const style = {
  message:
    "shadow-xl mt-4 mb-4 py-2 px-3 rounded-tl-full rounded-tr-full max-w-lg",
  name: "absolute text-gray-600 text-xs bottom-0 right-0",
  textSent:
    "bg-[#395dff] text-white flex-row-reverse text-end float-right rounded-bl-full",
  textReceived: "bg-[#e5e5ea] text-black float-left rounded-br-full",
};

const Message = ({ message }) => {
  let time = new Date();
  const messageClass =
    message.uid === auth.currentUser.uid
      ? `${style.textSent}`
      : `${style.textReceived}`;

  return (
    <div className="message-main-container">
      <div className={`${style.message} ${messageClass}`}>
        <p className="message-text">{message.text}</p>
      </div>
      <span className={style.name}>
        {message.name}{" "}
        <span>
          {time.toLocaleString("en-US", {
            hour: "numeric",
            minute: "numeric",
            hour12: true,
          })}
        </span>{" "}
      </span>
    </div>
  );
};

export default Message;
